import React, { useRef, useState } from "react";
import axios from 'axios';

export const Gallery = (props) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraStarted, setIsCameraStarted] = useState(false);

  const toggleCamera = () => {
    if (!isCameraStarted) {
      startCamera();
    } else {
      takePicture();
    }
  };

  const startCamera = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        setIsCameraStarted(true);
      })
      .catch((err) => console.error('Error accessing the camera:', err));
  };

  const takePicture = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const video = videoRef.current;

    // Set canvas dimensions to match video dimensions
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Draw the current frame of the video onto the canvas
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Convert canvas image to Blob object representing a JPEG image
    canvas.toBlob((blob) => {
      if (!blob) {
        console.error('Failed to capture image');
        return;
      }

      // Create form data object
      const formData = new FormData();
      formData.append('image', blob, 'image.jpg');

      // Send image data to backend using Axios
      axios.post('http://127.0.0.1:5000/upload-image', formData)
        .then(response => {
          console.log('Image uploaded successfully:', response.data);
          // Optionally, handle the response from the server
        })
        .catch(error => {
          console.error('Error uploading image:', error);
          // Optionally, handle the error
        });
    }, 'image/jpeg');
  };

  return (
    <div id="portfolio" className="text-center">
      <div className="container">
        <div className="section-title">
          <h2>Demo</h2>
          <p>Industrial pipe counting using AI for inventory and invoice.</p>
        </div>
        <div className="row">
          <div className="demo">
            <video ref={videoRef} autoPlay />
            <canvas ref={canvasRef} width={640} height={480} style={{ display: "none" }} />
          </div>
          <button onClick={toggleCamera}>{isCameraStarted ? "Capture Image" : "Start Camera"}</button>
        </div>
      </div>
    </div>
  );
};
