import React from "react";
import logo from './logo.png';

export const Header = (props) => {
  return (
    <header id="header">
      <div className="intro">
        <div className="overlay">
          <div className="container">
            <div className="row align-items-center"> {/* Ensure vertical alignment */}
              <div className="col-md-4"> {/* Adjust the grid column size */}
                <img src={logo} style={{ maxHeight: '100%', transform: 'scale(0.4)' }} alt="Logo" />
              </div>
              <div className="col-md-8 intro-text">
                <h1>AI consulting and research</h1>
                <p>Creatively Inspiring lives...</p>
                <a href="#features" className="btn btn-custom btn-lg page-scroll">
                  Learn More
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};
