import React, { useRef, useState } from "react";

export const Gallery = (props) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [isCameraStarted, setIsCameraStarted] = useState(false);

  const toggleCamera = () => {
    if (!isCameraStarted) {
      startCamera();
    } else {
      takePicture();
    }
  };

  const startCamera = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        setIsCameraStarted(true);
      })
      .catch((err) => console.error('Error accessing the camera:', err));
  };
  const takePicture = () => {
    const canvas = canvasRef.current;
    const image = canvas.toDataURL('image/jpeg'); // Convert canvas image to base64 data URL

    // Send the image data to the backend
    fetch('http://your-backend-url/upload-image', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ image }), // Send the image data as JSON
    })
    .then((response) => {
      if (!response.ok) {
        throw new Error('Failed to upload image');
      }
      return response.json();
    })
    .then((data) => {
      console.log('Image uploaded successfully:', data);
      // Optionally, handle the response from the backend
    })
    .catch((error) => {
      console.error('Error uploading image:', error);
      // Optionally, handle the error
    });
  };


  return (
    <div id="portfolio" className="text-center">
      <div className="container">
        <div className="section-title">
          <h2>Demo</h2>
          <p>Industrial pipe counting using AI for inventory and invoice.</p>
        </div>
        <div className="row">
          <div className="demo">
            <video ref={videoRef} autoPlay />
            <canvas ref={canvasRef} width={640} height={640} style={{ display: "none" }} />
          </div>
          <button onClick={toggleCamera}>{isCameraStarted ? "Capture Image" : "Start Camera"}</button>
          {capturedImage && (
            <div>
            <h3>Annotated Image</h3>
            <img src={`data:image/jpeg;base64,${capturedImage}`} alt="Annotated" />
          </div>
          )}
        </div>
      </div>
    </div>
  );
};
